mapper.select:
        select u.username,u.address,o.number,i.name,i.price,o.createtime,x.total_amount
from user u,items i,orderdetail d,orders o,(select sum(zongjia) as total_amount,orders_id from
    (select d.orders_id,i.price*d.items_num AS zongjia from items i,orderdetail d where i.id=d.items_id )a GROUP BY a.orders_id)x
where u.id=o.user_id and o.id=d.orders_id and i.id=d.items_id and x.orders_id=o.id

            and u.username=#{username}


            and i.name=#{name}


            and o.createtime >= #{createtimeStart}


            and o.createtime <= #{createtimeEnd}


            and i.price >= #{createtimeEnd}


            and i.price <= #{priceEnd}
