mapper.select:
select u.username, u.address, o.number, i.name, i.price, o.createtime, od.items_num*i.price as totalAmount
from orders o
left outer join orderdetail od
on o.id = od.orders_id
inner join items i
on i.id = od.items_id
inner join user u
on o.user_id = u.id


        number = #{number}


        and name = #{name}


        and o.createtime between #{createtimeStart} and #{createtimeEnd}


        and price between #{priceStart} and #{priceEnd}

;
