mapper.select:
SELECT u.*, o.id, it.name, it.price, o.createtime, totall.num num FROM USER u, orders o
left JOIN ( SELECT od.orders_id, od.items_id, sum(it.price * od.items_num) num FROM orderdetail od, items it WHERE od.items_id = it.id GROUP BY od.orders_id, od.items_id ) totall ON o.id = totall.orders_id,
orderdetail od, items it WHERE u.id = o.user_id AND it.id = od.items_id AND od.orders_id = o.id and o.id = #{id} and it.`name` like concat('%', concat(#{name}, '%')) and o.createtime BETWEEN #{createtimeStart} and #{createtimeEnd} and it.price BETWEEN #{priceStart} and #{priceEnd};
